/* **********************************************
     Begin IdleModel.js
********************************************** */


// Namespace
/////////////////////////////////////////////

this.bwco			= this.bwco || { };
this.bwco.idle		= this.bwco.idle || { };


(function() {

	"use strict";


	// Constructor
	/////////////////////////////////////////////

	function IdleModel() {

		// Imports
		/////////////////////////////////////////////

		var Dispatcher		= bwco.events.Dispatcher,
			IdleEvent		= bwco.idle.IdleEvent;


		// Superclass
		/////////////////////////////////////////////

		Dispatcher.call(this);


		// Constants
		/////////////////////////////////////////////

		var DEFAULT_TIMEOUT_SECS	= 90;


		// Private properties
		/////////////////////////////////////////////

		var _self			= this;

		var _timeout,
			_secs			= DEFAULT_TIMEOUT_SECS,
			_idle			= false;


		// Getters & setters
		/////////////////////////////////////////////

		Object.defineProperties(this, {

			"idle": {
				get: function() {
					return _idle;
				}
			},

			"timeoutSecs": {
				get: function() {
					return _secs;
				},
				set: function(val) {
					if (val != _secs) {
						_secs	= val;
						resetTimeout();
					}
				}
			}

		})


		// Public methods
		/////////////////////////////////////////////

		this.startIdle = function() {

			clearTimeout(_timeout);

			if (!_idle) {
				_idle	= true;

				_self.dispatch(IdleEvent.IDLE);

			}

		}

		this.breakIdle = function() {

			resetTimeout();

			if (_idle) {
				_idle	= false;

				_self.dispatch(IdleEvent.BREAK);

			}

			_self.dispatch(IdleEvent.RESET);

		}


		// Constructor
		/////////////////////////////////////////////

		init();

		function init() {

			resetTimeout();

		}


		// Event handlers
		/////////////////////////////////////////////

		function onIdleTimeout() {

			_self.startIdle();

		}


		// Private methods
		/////////////////////////////////////////////

		function resetTimeout() {

			clearTimeout(_timeout);

			if (_secs > 0) {
				_timeout	= setTimeout(onIdleTimeout, _secs * 1000);
			}

		}


		// Helpers
		/////////////////////////////////////////////


	}


	// Inheritance
	/////////////////////////////////////////////

	bwco.utils.extend(IdleModel, bwco.events.Dispatcher);
	bwco.utils.makeSingleton(IdleModel);


	// Add to namespace
	/////////////////////////////////////////////

	bwco.idle.IdleModel		= IdleModel;


}());



