// Namespace
/////////////////////////////////////////////

this.bwco			= this.bwco || { };
this.bwco.geom		= this.bwco.geom || { };


(function() {

	"use strict";

	// Constructor
	/////////////////////////////////////////////

	function Area(x, y, w, h) {

		// Private properties
		/////////////////////////////////////////////

		var _self	= this;

		var Pt		= bwco.geom.Pt,
			Area	= bwco.geom.Area,
			Size	= bwco.geom.Size,
			Range	= bwco.math.Range;

		var _x		= x,
			_y		= y,
			_w		= w,
			_h		= h;


		// Getters & setters
		/////////////////////////////////////////////

		Object.defineProperties(this, {

			"x": {
				get: function() {
					return _x;
				},
				enumerable: true
			},
			"y": {
				get: function() {
					return _y;
				},
				enumerable: true
			},

			"width": {
				get: function() {
					return _w;
				},
				enumerable: true
			},
			"height": {
				get: function() {
					return _h;
				},
				enumerable: true
			},

			"xRange": {
				get: function() {
					return new Range(_x, _x + _w);
				}
			},
			"yRange": {
				get: function() {
					return new Range(_y, _y + _h);
				}
			},

			"centerX": {
				get: function() {
					return _self.xRange.lerp(.5);
				}
			},
			"centerY": {
				get: function() {
					return _self.yRange.lerp(.5);
				}
			},
			"centerPt": {
				get: function() {
					return new Pt(_self.centerX, _self.centerY);
				}
			},

			"randomX": {
				get: function() {
					return _self.xRange.random;
				}
			},
			"randomY": {
				get: function() {
					return _self.yRange.random;
				}
			},
			"randomPt": {
				get: function() {
					return new Pt(_self.randomX, _self.randomY);
				}
			},

			"topLeftPt": {
				get: function() {
					return new Pt(_x, _y);
				}
			},
			"topRightPt": {
				get: function() {
					return new Pt(_x + _w, _y);
				}
			},
			"bottomLeftPt": {
				get: function() {
					return new Pt(_x, _y);
				}
			},
			"bottomRightPt": {
				get: function() {
					return new Pt(_x, _y);
				}
			},

			"size": {
				get: function() {
					return new Size(_w, _h);
				}
			},

			"whRatio": {
				get: function() {
					return _self.size.whRatio;
				}
			},
			"hwRatio": {
				get: function() {
					return _self.size.hwRatio;
				}
			}

		});


		// Public methods
		/////////////////////////////////////////////

		this.clone = function() {
			return new Area(_x, _y, _w, _h);
		}

		this.clampPt = function(pt) {
			var px	= _self.xRange.clamp(pt.x);
			var py	= _self.yRange.clamp(pt.y);
			return new Pt(px, py);
		}

		this.toString = function() {
			return "(x: " + _x + ", y: " + _y + ", width: " + _w + ", height: " + _h + ")";
		}


	}


	// Static methods
	/////////////////////////////////////////////

	Area.fromXYs = function(x1, y1, x2, y2) {
		var w	= x2 - x1;
		var h	= y2 - y1;
		return new Area(x1, y1, w, h);
	}


	// Add to namespace
	/////////////////////////////////////////////

	bwco.geom.Area = Area;


}());




